import React from 'react';
import SearchUser from "../components/SearchUser";
import { withRouter, Link } from "react-router-dom"


class User extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            repos: [],
            username: ""
        };
    }

    componentDidMount() {
        console.log(this.props, "user");
        let { location } = this.props
        fetch(`https://api.github.com/users${this.state.user}/repos`)
            .then(response => response.json())
            .then(response => {
                this.setState({
                    user: response


                })
            })

    }




    render() {
        let { user } = this.state
        console.log(user, "reposirity");

        if (!this.state.user) {
            return (<div className="user-page">LOADING...</div>);
        }


        return (

            <div>
                <SearchUser onSearch={(username) => { this.setState({ username })}}/>

                <div >:


                    {/* <h2 className="user-info"> {user.full_name}</h2>

                    <p className="user-bio">{user.description}</p> */}

                    {user.map = (item) => {
                        return   <h1>{item.full_name}</h1>  

                    }}



                </div>
            </div>

        )

    }
}

export default withRouter(User)
