import React from 'react';
import SearchUser from "../components/SearchUser";
import { withRouter, Link } from "react-router-dom"


class User extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            repos: [],
            username: "",
        };
    }

    componentDidMount() {


    }
    fetchUser = (username) => {
        fetch(`https://api.github.com/users/${username}/repos`)
            .then(response => response.json())
            .then(response => {
                this.setState({
                    repos: response,
                    // repos: repos.push( response.description)
                })
            })
    }


    render() {
        let { username, repos } = this.state
        console.log('repository', repos, username);

        if (!repos.length) return (<div className="user-page">LOADING...</div>);

        return (
            <div>
                <SearchUser onSearch={this.handleData} fetchUser={this.fetchUser} />
                {
                    repos.map(item => {
                        return (
                            <div className="showUserData" key={item.id}  >
                                <h1>{item.name}</h1>
                                <p>{item.description}</p>
                            </div>
                        )
                    })

                }
                <p>{this.state.username}</p>
            </div>
        )
    }
}

export default User
