        super(props);
        this.state = {
            repos: [],
            username: "",
            user: []
        };
    }

    componentDidMount() {

        fetch(`https://api.github.com/users/${this.state.username}/repos`)
            .then(response => response.json())
            .then(response => {
                this.setState({
                    user: response,


