import React from 'react';
import SearchUser from "../components/SearchUser";
import { withRouter, Link } from "react-router-dom"


class User extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            repos: [],
            username: "",
            user: [],
        };
    }

    componentDidMount() {

        fetch(`https://api.github.com/users/${this.state.username}/repos`)
            .then(response => response.json())
            .then(response => {
                this.setState({
                    user: response,
                    // repos: repos.push( response.description)
                })
            })
    }
fetchUser = (user)
    handleData = (username) => {
        this.setState({
            ...username
        })

    }


    render() {
        let { username, repos, user } = this.state
        console.log(user, username, "repository");

        if (!this.state.user) {
            return (<div className="user-page">LOADING...</div>);
        }


        return (

            <div>
                <SearchUser onSearch={this.handleData} />
                {
                    username ?
                        user.map(item => {
                            return (
                                <div className="showUserData" key={item.id}  >
                                    <h1>{item.name}</h1>
                                    <p>{item.description}</p>
                                </div>

                            )
                        })
                        : ''

                }

                <p>{this.state.username}</p>





            </div>

        )

    }
}

export default User
