import React, { Component } from 'react';
import {withRouter} from   "react-router-dom"



class SearchUser extends Component {



    handleSubmit=(e)=>{
        e.preventDefault();
        this.props.onSearch(this.refs.userInput.value)
    }

    render() {
        console.log("searchuser", this.props);


        return (
            <div>
                <div className="search-page">
                    <h2>Enter a GitHub username</h2>
                    <form  onSubmit={this.handleSubmit}   >
                        <input ref="userInput" className="searchInput" type="text"   />
                        <button className="searchUser">Search</button>
                    </form>
                </div>

            </div>
        )
    }
}


export  default   withRouter( SearchUser)
