import React from 'react';


class UserList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: []


        };
    }

    componentDidMount() {
        fetch(`https://api.github.com/users/user/${this.props.params.username}/repos`)
            .then(response => response.json())
            .then(response => {
                this.setState({
                    users: response


                })
            })

    }




    render() {
        return (
            <div>
                <h1>List repositories for a user</h1>
                {console.log(this.state.users,"user")}
                <tbody>

                    {this.state.users.map(user=>{
                       return (<tr  key={user.id} >
                                    <td>{user.name}</td>
                                    <td>{user.description}</td>
                             </tr>)
                    })
                    }
                </tbody>


            </div>

        )

    }
}

export default UserList
